// Comprehensive Validation Rules
// Based on Validation Specialist agent specifications

const records = $input.all();
const validationReport = {
  total_records: records.length,
  passed: 0,
  flagged: 0,
  issues_by_type: {},
  issues_by_severity: { HIGH: 0, MEDIUM: 0, LOW: 0 }
};

const validated = [];

for (const item of records) {
  const record = item.json;
  const issues = [];
  let confidence = record.confidence || 0.9;

  // RULE 1: Calculation Verification
  // Formula: wages + ot + claims + allowance + commission = total_payment
  // Tolerance: ¬±RM 0.50
  const calculated = 
    (parseFloat(record.wages) || 0) +
    (parseFloat(record.ot) || 0) +
    (parseFloat(record.claims) || 0) +
    (parseFloat(record.allowance) || 0) +
    (parseFloat(record.commission) || 0);

  const stated = parseFloat(record.total_payment) || 0;
  const diff = Math.abs(calculated - stated);

  if (diff > 0.5) {
    const issue = {
      type: 'calculation_mismatch',
      severity: 'MEDIUM',
      expected: calculated.toFixed(2),
      found: stated.toFixed(2),
      difference: diff.toFixed(2),
      description: `Payment calculation mismatch: Expected RM${calculated.toFixed(2)}, Found RM${stated.toFixed(2)}`
    };
    issues.push(issue);
    confidence -= 0.15;
    validationReport.issues_by_type['calculation_mismatch'] = 
      (validationReport.issues_by_type['calculation_mismatch'] || 0) + 1;
    validationReport.issues_by_severity['MEDIUM']++;
  }

  // RULE 2: IC Format Validation
  // Format: YYMMDD-PB-###G (12 digits with dashes)
  // Example: 900101-01-1234
  const icPattern = /^\d{6}-\d{2}-\d{4}$/;
  if (record.ic) {
    if (!icPattern.test(record.ic)) {
      const issue = {
        type: 'invalid_ic_format',
        severity: 'HIGH',
        value: record.ic,
        expected: 'YYMMDD-PB-###G',
        description: `Invalid IC format: ${record.ic} (Expected: YYMMDD-PB-###G)`
      };
      issues.push(issue);
      confidence -= 0.25;
      validationReport.issues_by_type['invalid_ic_format'] = 
        (validationReport.issues_by_type['invalid_ic_format'] || 0) + 1;
      validationReport.issues_by_severity['HIGH']++;
    }
  }

  // RULE 3: Bank Number Validation
  // Requirements: Numeric only, 10-20 digits
  if (record.bank_no) {
    const bankNo = record.bank_no.toString().replace(/[\s-]/g, '');
    if (!/^\d{10,20}$/.test(bankNo)) {
      const issue = {
        type: 'invalid_bank_number',
        severity: 'MEDIUM',
        value: record.bank_no,
        description: `Invalid bank number format: ${record.bank_no} (Expected: 10-20 digits)`
      };
      issues.push(issue);
      confidence -= 0.1;
      validationReport.issues_by_type['invalid_bank_number'] = 
        (validationReport.issues_by_type['invalid_bank_number'] || 0) + 1;
      validationReport.issues_by_severity['MEDIUM']++;
    }
  }

  // RULE 4: Required Fields Check
  const required = ['fullname', 'ic', 'project_name', 'total_payment'];
  for (const field of required) {
    if (!record[field] || record[field] === '' || record[field] === null) {
      const issue = {
        type: 'missing_required_field',
        severity: 'HIGH',
        field: field,
        description: `Missing required field: ${field}`
      };
      issues.push(issue);
      confidence -= 0.3;
      validationReport.issues_by_type['missing_required_field'] = 
        (validationReport.issues_by_type['missing_required_field'] || 0) + 1;
      validationReport.issues_by_severity['HIGH']++;
    }
  }

  // RULE 5: Date Format Validation
  // Accepted: DD/MM/YYYY, YYYY-MM-DD, DD-MM-YYYY
  const dateFields = ['project_date', 'payment_date'];
  const datePattern = /^(\d{2}\/\d{2}\/\d{4}|\d{4}-\d{2}-\d{2}|\d{2}-\d{2}-\d{4})$/;
  for (const field of dateFields) {
    if (record[field] && !datePattern.test(record[field])) {
      const issue = {
        type: 'invalid_date_format',
        severity: 'LOW',
        field: field,
        value: record[field],
        description: `Invalid date format in ${field}: ${record[field]}`
      };
      issues.push(issue);
      confidence -= 0.05;
      validationReport.issues_by_type['invalid_date_format'] = 
        (validationReport.issues_by_type['invalid_date_format'] || 0) + 1;
      validationReport.issues_by_severity['LOW']++;
    }
  }

  // RULE 6: Currency Validation
  // Requirements: Positive numbers, max 2 decimal places
  const currencyFields = ['wages', 'hour_wages', 'ot', 'claims', 'allowance', 'commission', 'total_payment'];
  for (const field of currencyFields) {
    if (record[field] !== undefined && record[field] !== null && record[field] !== '') {
      const value = parseFloat(record[field]);
      if (isNaN(value) || value < 0) {
        const issue = {
          type: 'invalid_currency',
          severity: 'MEDIUM',
          field: field,
          value: record[field],
          description: `Invalid currency value in ${field}: ${record[field]}`
        };
        issues.push(issue);
        confidence -= 0.1;
        validationReport.issues_by_type['invalid_currency'] = 
          (validationReport.issues_by_type['invalid_currency'] || 0) + 1;
        validationReport.issues_by_severity['MEDIUM']++;
      }
    }
  }

  // RULE 7: Completeness Score
  // Check what percentage of optional fields are filled
  const optionalFields = ['bank', 'bank_no', 'project_date', 'project_time', 'working_time', 'project_pic', 'project_venue'];
  const filledOptional = optionalFields.filter(f => record[f] && record[f] !== '').length;
  const completeness = filledOptional / optionalFields.length;

  if (completeness < 0.5) {
    const issue = {
      type: 'low_completeness',
      severity: 'LOW',
      completeness: (completeness * 100).toFixed(0) + '%',
      description: `Low data completeness: ${(completeness * 100).toFixed(0)}% of optional fields filled`
    };
    issues.push(issue);
    confidence -= 0.05;
  }

  // Determine final validation status
  const highSeverityCount = issues.filter(i => i.severity === 'HIGH').length;
  const mediumSeverityCount = issues.filter(i => i.severity === 'MEDIUM').length;

  let validation_status;
  if (highSeverityCount > 0) {
    validation_status = 'flagged';
  } else if (mediumSeverityCount > 2) {
    validation_status = 'flagged';
  } else if (issues.length > 0) {
    validation_status = 'warning';
  } else {
    validation_status = 'passed';
  }

  // Update report counters
  if (validation_status === 'passed' || validation_status === 'warning') {
    validationReport.passed++;
  } else {
    validationReport.flagged++;
  }

  validated.push({
    json: {
      ...record,
      validation_status,
      confidence: Math.max(0, Math.min(1, confidence)),
      validation_issues: issues,
      issue_count: issues.length,
      high_severity_issues: highSeverityCount,
      medium_severity_issues: mediumSeverityCount,
      validation_timestamp: new Date().toISOString()
    }
  });
}

// Add summary as first item
validated.unshift({
  json: {
    _summary: true,
    ...validationReport
  }
});

return validated;

// Generate Validation Report
const items = $input.all();
const summary = items[0].json;  // First item is summary
const records = items.slice(1);  // Rest are validated records

// Group issues by type
const issuesByType = {};
for (const item of records) {
  for (const issue of item.json.validation_issues || []) {
    if (!issuesByType[issue.type]) {
      issuesByType[issue.type] = [];
    }
    issuesByType[issue.type].push({
      row: item.json.source_row,
      fullname: item.json.fullname,
      ...issue
    });
  }
}

// Build markdown report
let markdown = `# Data Validation Report\n\n`;
markdown += `**Generated:** ${new Date().toISOString()}\n`;
markdown += `**Total Records:** ${summary.total_records}\n\n`;
markdown += `---\n\n`;

markdown += `## Executive Summary\n\n`;
markdown += `### Overall Quality Score: ${((summary.passed / summary.total_records) * 100).toFixed(1)}%\n\n`;
markdown += `- **Passed:** ${summary.passed} records (${((summary.passed / summary.total_records) * 100).toFixed(1)}%)\n`;
markdown += `- **Flagged:** ${summary.flagged} records (${((summary.flagged / summary.total_records) * 100).toFixed(1)}%)\n\n`;

markdown += `### Issues by Severity\n`;
markdown += `- üî¥ **HIGH:** ${summary.issues_by_severity.HIGH} issues\n`;
markdown += `- üü° **MEDIUM:** ${summary.issues_by_severity.MEDIUM} issues\n`;
markdown += `- üü¢ **LOW:** ${summary.issues_by_severity.LOW} issues\n\n`;

markdown += `---\n\n`;

markdown += `## Issues Found\n\n`;
for (const [type, issues] of Object.entries(issuesByType)) {
  markdown += `### ${type.replace(/_/g, ' ').toUpperCase()} (${issues.length} records)\n\n`;
  markdown += `| Row | Name | Details |\n`;
  markdown += `|-----|------|---------|\n`;
  for (const issue of issues.slice(0, 10)) {  // Show first 10
    markdown += `| ${issue.row} | ${issue.fullname || 'N/A'} | ${issue.description} |\n`;
  }
  if (issues.length > 10) {
    markdown += `\n*...and ${issues.length - 10} more*\n`;
  }
  markdown += `\n`;
}

markdown += `---\n\n`;
markdown += `## Recommendations\n\n`;

if (summary.issues_by_severity.HIGH > 0) {
  markdown += `‚ö†Ô∏è **${summary.issues_by_severity.HIGH} HIGH severity issues** must be resolved before import.\n\n`;
}

if (summary.issues_by_severity.MEDIUM > 5) {
  markdown += `üîç Review ${summary.issues_by_severity.MEDIUM} MEDIUM severity issues for data quality.\n\n`;
}

if (summary.flagged === 0) {
  markdown += `‚úÖ All records passed validation! Ready for import.\n`;
} else {
  markdown += `üìã **Next Steps:**\n`;
  markdown += `1. Review flagged records in detail\n`;
  markdown += `2. Fix HIGH severity issues\n`;
  markdown += `3. Consider re-analyzing low confidence rows\n`;
  markdown += `4. Proceed with dry-run import\n`;
}

return [{
  json: {
    summary: summary,
    markdown_report: markdown,
    issues_by_type: issuesByType,
    timestamp: new Date().toISOString()
  }
}];
